import React from 'react';
import { Link } from 'react-router-dom';
import { compose } from 'recompose'

import { AuthUserContext } from '../Session';
import SignOutButton from '../SignOut';
import * as ROUTES from '../../constants/routes';
import * as ROLES from '../../constants/roles';

import 'bootstrap/dist/css/bootstrap.min.css'
import {Navbar} from 'react-bootstrap'

const Navigation = () => (
  <AuthUserContext.Consumer>
    {authUser =>authUser ? (<NavigationAuth authUser={authUser} />) : (<NavigationNonAuth />)
    }
  </AuthUserContext.Consumer>
);

const NavigationAuth = ({ authUser }) => (
  <ul>
    <li>
      <Link to={ROUTES.LANDING}>Landing</Link>
    </li>
    <li>
      <Link to={ROUTES.HOME}>Home</Link>
    </li>
    <li>
      <Link to={ROUTES.ACCOUNT}>Account</Link>
    </li>
    {!!authUser.roles[ROLES.ADMIN] && (
      <li>
        <Link to={ROUTES.ADMIN}>Admin</Link>
      </li>
    )}
    <li>
      <SignOutButton />
    </li>
  </ul>
);

const NavigationNonAuth = () => (
  <div>

    <Navbar class="navbar navbar-dark navbar-expand-sm fixed-top">
        <div class="container">
            <a class="navbar-brand mr-auto" href="#">BoiPoka</a>
            <div class="collapse navbar-collapse" id="Navbar">   
                <ul class="navbar-nav mr-auto">
                    <li class="nav-item  active"><t><Link to={ROUTES.HOME}>Home</Link></t></li>
                    <li class="nav-item"><a class="nav-link" href={ROUTES.HOME}>Home2</a></li>
                    <li class="nav-item"><Link to={ROUTES.SIGN_IN}>Sign In</Link></li>
                </ul>
            </div>     
        </div>
     </Navbar>

    
  </div>
  
  
);

export default Navigation;
