import React from 'react';
import { Link } from 'react-router-dom';
import { compose } from 'recompose'

import { AuthUserContext } from '../Session';
import SignOutButton from '../SignOut';
import * as ROUTES from '../../constants/routes';
import * as ROLES from '../../constants/roles';

import 'bootstrap/dist/css/bootstrap.min.css'
import '../styles.css'
import {Navbar,footer} from 'react-bootstrap'

const Navigation = () => (
  <AuthUserContext.Consumer>
    {authUser =>authUser ? (<NavigationAuth authUser={authUser} />) : (<NavigationNonAuth />)
    }
  </AuthUserContext.Consumer>
);

const NavigationAuth = ({ authUser }) => (
  <ul>
    <li>
      <Link to={ROUTES.LANDING}>Landing</Link>
    </li>
    <li>
      <Link to={ROUTES.HOME}>Home</Link>
    </li>
    <li>
      <Link to={ROUTES.ACCOUNT}>Account</Link>
    </li>
    {!!authUser.roles[ROLES.ADMIN] && (
      <li>
        <Link to={ROUTES.ADMIN}>Admin</Link>
      </li>
    )}
    <li>
      <SignOutButton />
    </li>
  </ul>
);

const NavigationNonAuth = () => (
  
  <div class="container">
    <nav class="navbar-dark navbar-expand-sm fixed-top">
        <div class="container">
            <a class="navbar-brand mr-auto" href="#">BoiPoka</a>
            <div class="collapse navbar-collapse" id="Navbar">
              <a class="nav-item  active nav-link" href={ROUTES.HOME}>Home</a> 
              <a class="nav-item nav-link" href={ROUTES.SIGN_IN}>Sign In</a>  
            </div>     
        </div>
     </nav> 
  </div> 
  
  
  
);

export default Navigation;
